/**
 *  @class
 *  @function svg-divider
 */
if (!customElements.get('svg-divider')) {
	class SvgDivider extends HTMLElement {
		constructor() {
			super();
		}
		connectedCallback() {
			const _this = this,
				path = _this.querySelector('path.path-anim'),
				pathTo = path.dataset.pathTo;

			gsap.timeline({
				scrollTrigger: {
					trigger: _this,
					start: 'top bottom',
					end: 'top top',
					scrub: true
				}
			})
				.to(path, {
					ease: 'none',
					attr: { d: pathTo }
				});
		}
	}
	customElements.define('svg-divider', SvgDivider);
}
