/**
 *  @class
 *  @function LoginForm
 */
if (!customElements.get('login-form')) {
  class LoginForm extends HTMLElement {
    constructor() {
      super();


    }

    connectedCallback() {
      this.ul = this.querySelector('ul');
      this.a = this.ul.querySelectorAll('a');
      this.forget = this.querySelectorAll('.forget-password-toggle');
      this.container = this.querySelector('.thb-form-container');
      this.login = this.querySelector('.thb-login-form');
      this.setupEventListeners();
    }
    setupEventListeners() {
      this.a.forEach((link) => {
        link.addEventListener('click', (e) => {
          [].forEach.call(this.a, function (el) {
            el.classList.remove('active');
          });
          link.classList.add('active');

          this.container.classList.toggle('register-active');

          e.preventDefault();
        });
      });
      this.forget.forEach((link) => {
        link.addEventListener('click', (e) => {
          this.login.classList.toggle('forgot');
          e.preventDefault();
        });
      });
    }
  }
  customElements.define('login-form', LoginForm);
}
