/**
 *  @class
 *  @function MasonryBlog
 */
if (typeof MasonryBlog == 'undefined') {
  class MasonryBlog {
    constructor() {
      this.element = document.querySelector('.masonry');
      this.args = {
        layoutMode: 'masonry',
        itemSelector: '.item'
      };

      this.masonry = new Isotope('.masonry', this.args);

      imagesLoaded(this.element, this.update);
      this.update = () => {
        this.masonry.layout();
      };

      // Note: Instead of waiting for all images until we initialize the widget
      // we use event capturing to update the widget's layout progressively.
      this.element.addEventListener('load', this.update, true);
    }

  }
  new MasonryBlog();
}
